package com.eastrobot.robotdev.utils;import java.io.BufferedInputStream;import java.io.File;import java.io.FileInputStream;import java.io.FileOutputStream;import java.io.IOException;import java.io.InputStream;import java.io.OutputStream;import java.util.Properties;/** *  * @author james * 解析app.properties配置文件中的内容 * */public class PropertiesUtil {	private static PropertiesUtil p;	private static Properties prop;		private Properties getProperties() {		System.out.println("--------读取配置文件---------");		System.out.println(getClass().getResource("/").getPath());		String file = getClass().getResource("/").getPath() + "app.properties";		prop = new Properties();		InputStream is;		try {			File f = new File(file);			FileInputStream fis = new FileInputStream(f);			is = new BufferedInputStream(fis);			prop.load(is) ;			if(fis!=null)				fis.close();			if(is!=null)				is.close();		} catch (Exception e) {			e.printStackTrace();		}		return prop;	}	public static String getValueByKey(String key){		if(p==null){			p=new PropertiesUtil();		}		if(prop==null){			prop=p.getProperties();		}		String value="";		value=prop.getProperty(key);		return value;	}	}